import React, {useEffect, useState} from "react";
import axios from "axios";
import Title from "../Title/Title";
import ServiceBtn from './Service_Button';
// import './My_Post.css';

function ServiceDetail() {
    const [content, setContent] = useState('');
    const serviceNum = localStorage.getItem('detailBookingId');
    useEffect(() => {
        axios({
            method: "get",
            url: "http://54.180.210.232/api/v1/services/" + localStorage.getItem('businessType') + '/' + localStorage.getItem('detailBookingId'),
            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbUBhZG1pbi5jb20iLCJhdXRoIjoiUk9MRV9BRE1JTiIsImV4cCI6MTY3NDU3NjMyMn0.k3nRi0GQFQLep4f27QT282v7xtxtFfJ1dU4NfoewJ9FBIscv42klG-BJaFZZm1Itjxre4tWkapnYcffQi6EENA"
            },
        }).then((res) => {
            // console.log(res.data.message);
            // console.log(res.data.result);
            setContent(res.data.result);
            console.log(content);
        }).catch((error) => {
            alert("어쩌지이... ? 안 뜨는데에..");
        });
    },[])

    return (
        <div>
            <div className={"Service_Top_Wrap"}>
                <div className="ServiceWrap">
                    <Title title="서비스 내용" />
                    <h3 id="categoryTitle">카테고리</h3>
                    <ServiceBtn content={content.businessTypeResponse}/>
                    <h3 id="categoryTitle">학위</h3>
                    <ServiceBtn content={content.degreeRequest}/>
                    <h3 id="categoryTitle">서비스내용</h3>
                    <ServiceBtn content={content.educationContentRequest}/>
                    <h3 id="categoryTitle">추가요청사항</h3>
                    <div className={"Service_content_box"}>
                        {content.userContent == '' ? '추가 요청 사항 없음' : content.userContent}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ServiceDetail;
